function filterProjects(e) {
  const projects = document.querySelectorAll(".proyect-list div");
  const buttons = document.querySelectorAll(".filter-option");

  let filter = e.target.dataset.filter;

  if (filter === '*') { // Si pulsamos el botÃ³n de All
    
    // Mostramos los proyectos
    projects.forEach(project => project.classList.remove('hidden'));
    
    changueColorButton(0) ;
    
    document.getElementById("project-title").innerText = "Todos los Proyectos";

  }  
  
  else {

    projects.forEach(project => { // Recorremos todos los proyectos

      project.classList.contains(filter) ? 
      project.classList.remove('hidden') : 
      project.classList.add('hidden');

      if (filter === "personales")
      {

        document.getElementById("project-title").innerText = "Proyectos Personales";
        changueColorButton(1) ;

      }

      if (filter === "profesionales")
      {

        document.getElementById("project-title").innerText = "Proyectos Profesionales";
        changueColorButton(2) ;
      }


    });
  };

  // Cambiar el texto de Proyectos

};

function changueColorButton(buttonNumber)
{

  const buttons = document.querySelectorAll(".filter-option");
  for (var i = 0 ; i < buttons.length ; i++) // Recorremos el array
  {

    if (i == buttonNumber)
    {

      buttons[i].classList.add('buttonFocusOnClick') ;

    }

    else 
    {

      buttons[i].classList.remove('buttonFocusOnClick') ;

    }

  }

}

